import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    width: 300px;
    height: 65vh;
    padding: 20px;
    margin: 20px;
    border-radius: 20px;
    -webkit-box-shadow: 0px 0px 18px -6px #4E65FF; 
    box-shadow: 0px 0px 18px -6px #4E65FF;
`;

const Top = styled.div`
    height: 20%;
`;

const Image = styled.img`
    float: left;
    border-radius: 5px;
    width: 100%;
    height: 80%;
    object-fit: cover;
`;

const Title = styled.h1`
    font-size: 24px;
    text-align: center;
`;

const Text = styled.p`
    font-size: 20px;
    margin-top: 10px;
`;

const Card = (props) => {
  return (
    <Container>
        <Top>
            <Title>{props.title}</Title>
            <Text>{props.description}</Text>
        </Top>
        <Image src={`../img/${props.img}`}/>
    </Container>
  )
}

export default Card
