import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    height: 90%;
    background: url('https://www.nicepng.com/png/full/57-578538_confetti-pattern.png');
`;

const Wrapper = styled.div`
    height: 100%;
    padding: 20px;
    display: flex;

`;

const FormContainer = styled.div`
    width: 50%;
`;

const Title = styled.h1`
    margin: 50px;
    margin-top: 0;
`;

const Form = styled.form`
    height: 250px;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const LeftForm = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    margin-right: 20px;
`;

const RightForm = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`;

const Button = styled.button`
  border: none;
  border-radius: 5px;
  color: white;
  background-color: #4451d8;
  padding: 15px;
  margin-top: 20px;
  font-size: 20px;
  letter-spacing: 2px;
  cursor: pointer;
`;

const Input = styled.input`
    width: 200px;
    padding: 20px;
`;

const TextArea = styled.textarea`
    width: 200px;
    height: 60%;
    padding:20px;
`;

const AddressContainer = styled.div`
    width: 50%;
`;

const Contact = () => {
  return (
    <Container>
        <Wrapper>
            <FormContainer>
            <Title>Questions? <br/> Let's get in touch</Title>
                <Form>
                <LeftForm>
                    <Input placeholder='Your Name'/>
                    <Input placeholder='Your Phone'/>
                    <Input placeholder='Category of sweet'/>
                </LeftForm>
                <RightForm>
                    <TextArea placeholder='What do you want?'/>
                    <Button>Send</Button>
                </RightForm>
                </Form>
            </FormContainer>
            <AddressContainer>kjnk</AddressContainer>
        </Wrapper>
    </Container>
  )
}

export default Contact
